package com.ms.jun13maintence.main;

public class Point {
	private int price;
	private double point;
	private double sumpoint;
	public Point() {
		// TODO Auto-generated constructor stub
	}
	public Point(int price, double point, double sumpoint) {
		super();
		this.price = price;
		this.point = point;
		this.sumpoint = sumpoint;
	}
	public int getPrice() {
		return price;
	}
	public void setPrice(int price) {
		this.price = price;
	}
	public double getPoint() {
		return point;
	}
	public void setPoint(double point) {
		this.point = point;
	}
	public double getSumpoint() {
		return sumpoint;
	}
	public void setSumpoint(double sumpoint) {
		this.sumpoint = sumpoint;
	}
	
	
	
	
	

}
